	movia r17, 0x1920
#	addi r17, r4, 12 #pos enable coprocessador
	movia r7, 0 #contador
	movia r16, 4#contador2
	movia r10, 4 #contador
	movia r11, 3 #contador
	movia r12, 2 #contador
	movia r13, 1 #contador
	movia r8, 32 #valor a ser deslocado
	movia r15, 0xffffffff #enable valor
	movia r3, 4096
	
	loop1:
		ldw r9, 0(r2) #carregando word que contém o pixel
		
		bne r16, r10, desloc1
		roli r9, r9, 8
		desloc1:
		
		
		bne r16, r11, desloc2
		roli r9, r9, 16
		desloc2:
		
		
		bne r16, r12, desloc3
		roli r9, r9, 24
		desloc3:
		
		
		bne r16, r13, desloc4
		roli r9, r9, 0
		desloc4:
		
		stw r15, 0(r6) #colocando 1 no enable
		stw r7, 0(r5) #colocando contador no valor de endereco
		stw r9, 0(r4) #coloanco pixel na saida dos dados
		addi r7, r7, 1 # incrementando contador
#		stw r0, 0(r6) #colocando 0 no enable
		subi r16, r16, 1 #subtraido contador de pixels na word
		bne r16, r0, pular2 #se os pixels chegarem a 0 coloca 4 no contador de novo e incrementa o endereco
		addi r2, r2, 4
		movia r16, 4
		pular2: 
		beq r7, r3, fim #se o contador chegar a 4096 acaba
		br loop1

fim:
	stw r0, 0(r6) #colocando 0 no enable
	stw r15, 0(r17)